mapper.update:
update tb_cheer
set cheerNotify=1
where cheerNo = #{cheerParentNo}

update tb_cheer
set cheerContent=#{cheerContent}, cheerRegdate=sysdate
where cheerNo = #{cheerNo}

update tb_cheer
set cheerNotify=0
where cheerNo = #{_parameter}

mapper.select:
select *
from tb_cheer
where id=#{id} and projectNo=#{projectNo}
